let content = $json["message"]["content"];  // محتوى الرسالة من OpenAI

// استخراج البيانات باستخدام Regular Expressions (Regex)
let fullName = content.match(/1\.\s*Full name of the candidate:\s*([^\n]+)/);
let email = content.match(/2\.\s*Email address:\s*([^\n]+)/);
let education = content.match(/3\.\s*Education:\s*([^\n]+)/);
let yearsOfExperience = content.match(/4\.\s*Years of experience:\s*([^\n]+)/);
let fieldOfExpertise = content.match(/5\.\s*Field of expertise:\s*([^\n]+)/);
let skills = content.match(/6\.\s*Skills:\s*([\s\S]*?)(?=\n7\.)/);
let contactNumber = content.match(/7\.\s*Contact number:\s*([^\n]+)/);


// دالة مساعدة لتنظيف أي قيمة
const cleanValue = (val) => {
  if (!val || !val[1]) return "not found";
  const trimmed = val[1].trim();
  return trimmed === "" || trimmed === "---" ? "not found" : trimmed;
};

// دالة خاصة لتنظيف المهارات لأنها تحتاج تنسيق
const cleanSkills = (val) => {
  if (!val || !val[1]) return "not found";
  const cleaned = val[1].replace(/\n/g, ', ').trim();
  return cleaned === "" || cleaned === "---" ? "not found" : cleaned;
};

// إرجاع القيم المستخرجة والمعدلة
return {
  "Full name": cleanValue(fullName),
  "Email": cleanValue(email),
  "Education": cleanValue(education),
  "Years of experience": cleanValue(yearsOfExperience),
  "Field of expertise": cleanValue(fieldOfExpertise),
  "Skills": cleanSkills(skills),
  "Contact number": cleanValue(contactNumber)
};

